import React,{forwardRef,useRef}from 'react';

function Parent(props){
  const nameRef=useRef(null);
  const locRef=useRef(null);
  const setName=()=> {
    nameRef.current.value="Sachin"
  }
  return(
    <div>
                <p>
                    <input ref={nameRef} />
                    <input type="button" value="SetName" onClick={setName} />
                </p>
                <p>
                    <input ref={locRef} />
                    <Child ref={locRef} />
                </p>
                </div>
  );
}
export default Parent;

const Child = forwardRef((props, ref) => {
  const setLocation = () => {
      ref.current.value = "Mumbai";
  };
  return (
      <>
      <input type="button" value="setLocation" onClick={setLocation} />
      </>
  );
  });
